SELECT * FROM PARAMETER_MANAGEMENT_SERVI.PRODUCT_INFO_TNC_ENG:
{
  "faqList": [
    {
      "questionKey": "Apakah pembukaan dan penutupan tabungan autosave dapat dilakukan melalui outlet?",
      "answerKey": "Tidak, pembukaan dan penutupan Autosave hanya dapat dilakukan melalui BSI Mobile."
    },
    {
      "questionKey": "Bagaimana apabila saya gagal melakukan break Tabungan Autosave?",
      "answerKey": "Dalam proses break Tabungan Autosave, ada biaya administrasi break sebesar Rp25.000 yang didebet dari rekening Autosave. Pastikan ketika akan melakukan break, dana dalam Autosave mencukupi untuk biaya break tersebut. Jika masih error dalam melakukan break melalui BSI Mobile, dapat dibantu cabang terdekat untuk melakukan penutupan Autosave"
    },
    {
      "questionKey": "Apabila saya melakukan break Tabungan Autosave, bagaimana cara saya mengambil dana dalam rekening Autosave?",
      "answerKey": "Dana dalam rekening Tabungan Autosave akan otomatis dicairkan ke rekening sumber dana, setelah dipotong dengan biaya administrasi break."
    },
    {
      "questionKey": "Apakah Autosave diberikan buku tabungan?",
      "answerKey": "Tidak, namun bagi nasabah yang ingin memiliki buku tabungan, dapat meminta buku tabungan melalui outlet. Buku tabungan yang akan diberikan tergantung dari pilihan nasabah dalam memilih basic Tabungan Autosave (Wadiah atau Mudharabah)."
    },
    {
      "questionKey": "Bagaimana apabila pada rekening sumber nasabah tidak tersedia dana pada periode tertentu?",
      "answerKey": "Proses retrial pendebetan dana Autosave secara sistem hanya akan dilakukan 1x pada periode tersebut, dana tidak terakumulasi pada periode selanjutnya, dan autodebet akan tetap berjalan sesuai periodenya sampai jangka waktu selesai. Namun nasabah dapat melakukan setoran diluar jadwal sewaktu-waktu dengan cara pindah buku atau transfer manual."
    }
  ]
}

